import React, { Component } from 'react';
import './App.css';

const generos = ['Hombre', 'Mujer', 'Helicoptero Apache'];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      gender: null,
      birthdate: ''
    };
  }

  renderGenerosFor = () => {
    const generosJSX = [];
    for (let i = 0; i < generos.length; i++) {
      const genero = generos[i];
      generosJSX.push(
        <div>
          <input className='App-Form-Item'
            name='genders'
            required
            type='radio'
            value={genero}
            onChange={this.handleChange('gender')}
             />{genero}
      </div>)
    }
    return generosJSX;
  }

    handleChange = (key) => ({ target: { value } }) => {
      this.setState({ [key]: value });
    }

    handleSubmit = () => {
      const { firstName, lastName, email, gender, birthdate } = this.state;
      alert(`${firstName} ${lastName} ${email} ${gender} ${birthdate}`)
    }

    render() {
      const { firstName, lastName, email, birthdate } = this.state;
      return (
        <div className='App'>
          <form className='App-Form' onSubmit={this.handleSubmit}>
            <input className='App-Form-Item' required value={firstName} onChange={this.handleChange('firstName')} /> Nombre
            <input className='App-Form-Item' required value={lastName} onChange={this.handleChange('lastName')} /> Apellido
            <input className='App-Form-Item' required type='email' value={email} onChange={this.handleChange('email')} /> Correo
            {this.renderGenerosFor()}
            <input className='App-Form-Item' required type='date' value={birthdate} onChange={this.handleChange('birthdate')} /> Cumplea√±os
            <button className='App-Form-Item' type='submit'>{'Submit'}</button>
          </form>
        </div>
      );
    }
  }

  export default App;
